$bg-color,
$boedr-color in (primary,#004085, #cce5ff, #b8daff),
(success,#155724, #d4edda,#c3e6cb),
(danger,#721c24,#f8d7da,#f5c6cb) {
    .alerts-#{$classes} {
        color: $color;
        background-color: $bg-color;
        border-color: $boedr-color;
    }
}
